import React from 'react';
import './intro.css';
import bg from '../../assets/image.png';
import { Link } from 'react-scroll';
import btnImg from '../../assets/hireme.png';

const Intro = () => {
  return (
   <section id="intro">
    <div className="introcontent">
        <span className="hello">Hello</span>
        <span className="introtext">I'am <span className="introName">Zaid</span><br />Website Designer</span>
        <p className="introPara">I am a skilled web designer with experience in creating <br></br> visually appealing and user friendli Website</p>
        <Link><button className="btn"><img src={btnImg} alt="" className = "btnImg" />Hire Me</button></Link>
       
    </div>
    <img src={bg} alt="Profile" className="bg" />
     
   </section>
  )
}

export default Intro
